<script>
import BtnRed from "../ui/BtnRed.vue";
import gsap from "gsap";
import { MotionPathPlugin } from "gsap/MotionPathPlugin";
import ScrollTrigger from "gsap/ScrollTrigger";
export default {
  name: "HomeTestBlock",
  components: {
    BtnRed,
  },

  data() {
    return {
      theme: {
        name: "Наука",
        text: "Переходи на нашу страничку «Наука» прямо сейчас",
        link: "/nauka",
      },
      timeline: null,
    };
  },

  mounted() {
    gsap.registerPlugin(ScrollTrigger, MotionPathPlugin);
    this.timeline = gsap.timeline();
    gsap.to("#circle", {
      scrollTrigger: {
        trigger: ".decor",
        start: "top 80%", // Когда элемент войдет в 80% от высоты экрана
        toggleActions: "play none none none", // Анимация проигрывается один раз
      },
      rotation: 360,
      scale: 0, 
      duration: 1, 
      ease: "power1.inOut", 
      onComplete: () => {
        gsap.to(".hiddenPath", {
          opacity: 1,
          duration: 0.5,
          ease: "power1.inOut",
        });
        gsap.to(".message--1", {
          opacity: 1,
          scale: 1,
          duration: 0.5,
          delay: 0.8,
        });
        gsap.to(".message--2", {
          opacity: 1,
          scale: 1,
          duration: 0.5,
          delay: 1.2,
        });
      },
    });
  },
};
</script>

